package icu.miroirs.util;import com.alibaba.fastjson.JSONObject;import org.apache.commons.io.IOUtils;import org.junit.Test;import java.io.ByteArrayInputStream;import java.io.IOException;import java.io.InputStream;import java.net.URL;import java.nio.charset.StandardCharsets;import java.util.Properties;/** * @Auther: ripley * @Date: 2021/9/3 09:32 * @Description: */public class Properties2JsonUtilTest {    @Test    public void prop2JsonStr() throws IOException {        URL configUrl = getClass().getClassLoader().getResource("prop2json.properties");        String anyString = IOUtils.toString(configUrl);        InputStream result = new ByteArrayInputStream(anyString.getBytes(StandardCharsets.UTF_8));        Properties properties = new Properties();        properties.load(result);        JSONObject jsonObject = Properties2JsonUtil.prop2Json(properties);        System.out.printf(jsonObject.toJSONString());    }}